import { useState } from "react";
import { useEffect } from "react/cjs/react.development";
import "../App.css";
import { v4 as uuidv4 } from "uuid";
import { compareAsc, format } from "date-fns";

function Form(props) {
  const {
    titleInput,
    setTitleInput,
    laundryArray,
    setLaundryArray,
    noteInput,
    setNoteInput,
    formData,
    setFormData,
    isFormComplete,
    parent,
  } = props;

  const handleChangeLaundry = (e) => {
    setTitleInput(e.target.value);
  };

  const handleChangeNote = (e) => {
    setNoteInput(e.target.value);
  };

  const handleSubmit = () => {
    const finalData = formData;
    finalData["createdDate"] = format(new Date(), "dd-MM-yyyy HH:mm");

    if (parent === "App") {
      finalData["id"] = uuidv4();
    }

    if (parent === "List") {
      finalData["id"] = formData["id"];
    }

    setLaundryArray((prevState) => {
      return [finalData, ...prevState];
    });
    setTitleInput("");
    setNoteInput("");
    setFormData({});
  };

  useEffect(() => {
    console.log(formData);
  }, [formData]);

  return (
    <div>
      {/* Form */}
      <input
        onChange={handleChangeLaundry}
        type="text"
        placeholder="Title"
        value={titleInput}
      />
      <input
        onChange={handleChangeNote}
        type="text"
        placeholder="Your note..."
        value={noteInput}
      />
      {
        <button
          disabled={!isFormComplete}
          className={`submit-button-${!isFormComplete}`}
          onClick={handleSubmit}
        >
          Add
        </button>
      }
    </div>
  );
}

export default Form;
