import { useState } from "react/cjs/react.development";
import "../App.css";
import Form from "./Form";

function List(props) {
  const [isEditing, setIsEditing] = useState(false);
  const { notesArray, setNotesArray, setModalIsOpen } = props;

  const handleDelete = (e) => {

    let r = window.confirm("Are you sure you want to delete your note?");

    if (r == true) {
      const newArray = notesArray.filter((item) => {
        return item.id !== e.target.value;
      });
      setNotesArray(newArray);
    }
  };


  return (
    <div>
      {/* List */}
      {notesArray.map((item, index) => {
        return (
          <div key={item.dateInput + index} onClick={() => setModalIsOpen(true)}>
            {<div>{item.createdDate}</div>}
            {<div>{item.titleInput}</div>}
            {<div>{item.noteInput}</div>}

            <div>
              <button value={item.id} onClick={handleDelete}>
                Delete
              </button>
              {/* EDIT BUTTON */}
              {/* <button value={item.id} onClick={handleEdit}>
                Edit
              </button> */}
            </div>
            {isEditing === item.id && (
              <div>
                <input type="text" placeholder="Title" />
                <input type="text" placeholder="Your note..." />
              </div>

            )}
          </div>
        );
      })}
    </div>
  );
}

export default List;
