import { useEffect, useState } from "react";
import List from "./components/List";
import Form from "./components/Form";
import "./App.css";

function App() {
  const [titleInput, setTitleInput] = useState();
  const [noteInput, setNoteInput] = useState();
  const [formData, setFormData] = useState();
  const [laundryArray, setLaundryArray] = useState([]);
  const [isFormComplete, setIsFormComplete] = useState(false);

  useEffect(() => {
    if (
      formData &&
      formData["noteInput"]
    ) {
      setIsFormComplete(true);
    } else {
      setIsFormComplete(false);
    }
  }, [formData]);

  useEffect(() => {
    const newFormData = {
      titleInput: titleInput,
      noteInput: noteInput,
      workerInput: workerInput,

    };

    setFormData(newFormData);
  }, [titleInput, dateInput, noteInput, workerInput]);

  useEffect(() => {
    if (laundryArray) {
      console.log(laundryArray);
    }
  }, [laundryArray]);
  return (
    <div className="App">
      <Form
        titleInput={titleInput}
        setTitleInput={setTitleInput}
        setLaundryArray={setLaundryArray}
        dateInput={dateInput}
        setDateInput={setDateInput}
        noteInput={noteInput}
        setNoteInput={setNoteInput}
        workerInput={workerInput}
        setWorkerInput={setWorkerInput}
        formData={formData}
        setFormData={setFormData}
        isFormComplete={isFormComplete}
        parent="App"
      />
      <List laundryArray={laundryArray} setLaundryArray={setLaundryArray} />
    </div>
  );
}

export default App;
