import { useState } from "react";
import { useEffect } from "react/cjs/react.development";
import "../App.css";
import { v4 as uuidv4 } from "uuid";
import { compareAsc, format } from "date-fns";

function Form(props) {
  const {
    titleInput,
    setTitleInput,
    notesArray,
    setNotesArray,
    noteInput,
    setNoteInput,
    formData,
    setFormData,
    isFormComplete,
    parent,
  } = props;

  if (parent == "App") {
    const handleChangeTitle = (e) => {
      setTitleInput(e.target.value);
    };

    const handleChangeNote = (e) => {
      setNoteInput(e.target.value);
    };

    const handleSubmit = () => {
      const finalData = formData;
      finalData["createdDate"] = format(new Date(), "dd-MM-yyyy HH:mm");

      if (parent === "App") {
        finalData["id"] = uuidv4();
      }

      if (parent === "List") {
        finalData["id"] = formData["id"];
      }

      setNotesArray((prevState) => {
        return [finalData, ...prevState];
      });
      setTitleInput("");
      setNoteInput("");
      setFormData({});
    };

    //   useEffect(() => {
    //     console.log(formData);
    //   }, [formData]);

    return (
      <div className="form-wrapper">
        {/* Form */}
        <input
          onChange={handleChangeTitle}
          type="text"
          placeholder="Title"
          value={titleInput}
        />
        <textarea
          onChange={handleChangeNote}
          type="text"
          placeholder="Your note..."
          value={noteInput}
        />

        <button
          disabled={!isFormComplete}
          className={`submit-button-${!isFormComplete}`}
          onClick={handleSubmit}
        >
          Add
        </button>
      </div>
    );
  } else if (parent == "ModalList") {
    const handleChangeTitle = (e) => {
      setTitleInput(e.target.value);
    };

    const handleChangeNote = (e) => {
      setNoteInput(e.target.value);
    };

    const handleSubmit = () => {
      const finalData = formData;
      finalData["createdDate"] = format(new Date(), "dd-MM-yyyy HH:mm");

      if (parent === "App") {
        finalData["id"] = uuidv4();
      }

      if (parent === "List") {
        finalData["id"] = formData["id"];
      }

      setNotesArray((prevState) => {
        return [finalData, ...prevState];
      });
      setTitleInput("");
      setNoteInput("");
      setFormData({});
    };

    return (
      <div className="form-wrapper">
        {/* Form */}
        {notesArray.map((item, index) => {
          return (
            <div>
              <input onChange={handleChangeTitle} type="text" value="HOLA" />
              <textarea onChange={handleChangeNote}>{noteInput}</textarea>}
              {
                <button
                  disabled={!isFormComplete}
                  className={`submit-button-${!isFormComplete}`}
                  onClick={handleSubmit}
                >
                  Add
                </button>
              }
            </div>
          );
        })}
      </div>
    );
  }
}

export default Form;
