import { useState } from "react";
import { useEffect } from "react/cjs/react.development";
import "../App.css";
import { v4 as uuidv4 } from "uuid";
import { compareAsc, format } from 'date-fns'

function Form(props) {
  const {
    titleInput,
    setTitleInput,
    laundryArray,
    setLaundryArray,
    dateInput,
    setDateInput,
    noteInput,
    setNoteInput,
    workerInput,
    setWorkerInput,
    formData,
    setFormData,
    isFormComplete,
    parent,
  } = props;

  const handleChangeLaundry = (e) => {

    setTitleInput(e.target.value);
  };



  const handleChangeNote = (e) => {
    setNoteInput(e.target.value);
  };



  // useEffect(() => {
  //   // setFormData({});
  //   console.log(formData);
  // }, [laundryArray]);

  const handleSubmit = () => {
    const finalData = formData;
    finalData["createdDate"] = format(new Date(), 'dd-MM-yyyy HH:mm');


    if (parent === "App") {
      finalData["id"] = uuidv4();
    }

    if (parent === "List") {
      finalData["id"] = formData["id"];
    }

    setLaundryArray((prevState) => {
      return [finalData, ...prevState];
    });
    setTitleInput("");
    // setDateInput("");
    setNoteInput("");
    // setWorkerInput("");
    setFormData({});
  };

  useEffect(() => {
    console.log(formData);
  }, [formData]);

  return (
    <div>
      {/* Form */}
      <input
        onChange={handleChangeLaundry}
        type="text"
        placeholder="Title"
        value={titleInput}
      />
      {/* <input
        onChange={handleChangeDate}
        type="date"
        placeholder="When do you need it clean?"
        value={dateInput}
      /> */}
      <input
        onChange={handleChangeNote}
        type="text"
        placeholder="Your note..."
        value={noteInput}
      />
      {/* <input
        onChange={handleChangeWorker}
        type="text"
        placeholder="When do you need it clean?"
        value={workerInput}
      /> */}
      {/* {formData &&
        formData["titleInput"] &&
        formData["dateInput"] &&
        formData["noteInput"] &&
        formData["workerInput"] && (
          <button onClick={handleSubmit}>Submit</button>
        )} */}

      {
        <button
          disabled={!isFormComplete}
          className={`submit-button-${!isFormComplete}`}
          onClick={handleSubmit}
        >
          Add
        </button>
      }
    </div>
  );
}

export default Form;
