import { useState } from "react";
import { useEffect } from "react/cjs/react.development";
import "../App.css";
import { v4 as uuidv4 } from "uuid";
import { compareAsc, format } from "date-fns";

function Form(props) {
  const {
    titleInput,
    setTitleInput,
    notesArray,
    setNotesArray,
    noteInput,
    setNoteInput,
    formData,
    setFormData,
    isFormComplete,
    idSelected,
    parent,
  } = props;

  const handleChangeTitle = (e) => {
    setTitleInput(e.target.value);
  };

  const handleChangeNote = (e) => {
    setNoteInput(e.target.value);
  };

  const handleSubmit = () => {
    const finalData = formData;
    finalData["createdDate"] = format(new Date(), "dd-MM-yyyy HH:mm");

    if (parent === "App") {
      finalData["id"] = uuidv4();
    }

    if (parent === "List") {
      finalData["id"] = formData["id"];
    }

    if (parent === "ModalList") {
        finalData["id"] = formData["id"];
        final
      }

    setNotesArray((prevState) => {
      return [finalData, ...prevState];
    });
    setTitleInput("");
    setNoteInput("");
    setFormData({});
  };

  if (parent == "App") {
    return (
      <div className="form-wrapper">
        {/* Form */}
        <input
          onChange={handleChangeTitle}
          type="text"
          placeholder="Title"
          value={titleInput}
        />
        <textarea
          onChange={handleChangeNote}
          type="text"
          placeholder="Your note..."
          value={noteInput}
        />

        <button
          disabled={!isFormComplete}
          className={`submit-button-${!isFormComplete}`}
          onClick={handleSubmit}
        >
          Add
        </button>
      </div>
    );
  } else if (parent == "ModalList") {
    return (
      <div>
        {/* Form */}
         {notesArray.map((item) => {
             if (item.id === idSelected) {
          return (
        <div className="form-wrapper">
          <input onChange={handleChangeTitle} type="text" value={item.titleInput} />
          <textarea onChange={handleChangeNote}>{item.noteInput}</textarea>
          <button
            disabled={!isFormComplete}
            className={`submit-button-${!isFormComplete}`}
            onClick={handleSubmit}
          >
            Add
          </button>
        </div>
         );}
        })}
      </div>
    );
  }
}

export default Form;
